import axios from 'axios';

export const GETPOKEMON='GETPOKEMON'

export const GETPOKEMONAME='GETPOKEMONAME'
export const GETPOKEMONID='GETPOKEMONID'
export const GETPOKEMONTYPE='GETPOKEMONTYPE'


export const getPokemon= async()=>{ 
    return async function (dispatch) {
        return await axios.get("https://pokeapi.co/api/v2/pokemon")
          .then(response => dispatch({ type: GETPOKEMON, payload: response.data}))
        }
          
          
}
export const getPokemonName=async(dispatch)=>{ 
    const response = await axios.get("https://pokeapi.co/api/v2/pokemon");
    const data = await response.results;
    console.log(data);
    dispatch({
     type: "GETPOKEMONAME",
     payload: data,
   });
}
