    dispatch({
     type: "GETPOKEMONID",
     payload: data,
   });
}
export const getPokemonType=async(dispatch)=>{ 
    const response = await axios.get("https://pokeapi.co/api/v2/pokemon");
    const data = await response.results;
    console.log(data);
    dispatch({
     type: "GETPOKEMONTYPE",
     payload: data,
   });
}
