import { useDispatch, useSelector } from "react-redux";
import{getPokemon}from '../Store/action'
import Buscar from "./Buscar";
import './pokemon.css';
import PokemonCard from "./PokemonCard";


export function PokemonGrid(){
    
    const pokemones=useSelector(store=>store.pokemons)
    

    return(
        <div className="box">
            
            <h1>Pokemons</h1>
             <Buscar />
            
           
            <ul className="box">
                {
                    pokemones.map((i) => <PokemonCard src={i.image} name={i.name} id={i.id} image={i.imag}/> )
                            
                }
            </ul>
        </div>
    );
};
