import { getPokemonBase} from "../../Store/action";
import React, { useState } from 'react';
import axios from 'axios';
import { useEffect } from "react";
import { useDispatch} from "react-redux";
import './addPokemon.css';

export default function AddPokemon() {
  
    const dispatch=useDispatch();
  const [name, setName] = useState('');
  const [nameError, setNameError] = useState('');
  const [vida, setVida] = useState(0);
  const [vidaError, setVidaError] = useState('');
  const [error, setError] = useState('');
  const [fuerza, setFuerza] = useState(0);
  const [defensa, setDefensa] = useState(0);
  const [velocidad, setVelocidad] = useState(0);
  const [altura, setAltura] = useState(0);
  const [peso, setPeso] = useState(0);
  const [tipos, setTipo] = useState('');
  const [status, setStatus] = useState('');
  useEffect(() => {  
      dispatch(getPokemonBase(2))
    },[ ]);
  
 

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log(event);
    
    if(!name){
      setNameError('El nombre es requerido');
    }
    if(name.length<=10){
      setNameError('error max-longitude');
    }
     if(isNaN(vida)||isNaN(fuerza)||isNaN(defensa)||isNaN(velocidad)||isNaN(altura)||isNaN(peso)) {
      setVidaError('La vida debe ser un número');
    }if(vida.lenght>0||fuerza.lenght>0||defensa.lenght>0||velocidad.lenght>0||altura.lenght>0||peso.lenght>0){
      setError('debe ser mayor de 0')
    }
    else{
      axios.post('http://localhost:3001/pokemons/', {
        name,
        vida,
        fuerza,
        defensa,
        velocidad,
        altura,
        peso,
        tipos
      })
      .then(response => setStatus(response.status))
      .catch(error => console.error(error));
    }
    if(status===200) setStatus('pokemon creado')
  }

  return (
  <>
       
       <br />
       <br />
      <div className='creacion'>
        <h2 className=''>Crear Pokemon</h2>
      <form className='' onSubmit={handleSubmit}>
       
       <label className=''>
         Name:
         <input type="text" onChange={e => setName(e.target.value)} />
         {nameError && <h5 >{nameError}</h5>}
       </label>
       <br />
       <label className=''>
         Vida:
         <input type="number"  onChange={e => setVida(e.target.value)} />
         {vidaError && <h5 >{vidaError}</h5>}
         {error&&<h5>{error}</h5>}
       </label>
       <br />
       <label className=''>
         hp:
         <input type="number"  onChange={e => setFuerza(e.target.value)} />
         {vidaError && <h5 >{vidaError}</h5>}
         {error&&<h5>{error}</h5>}
       </label>
       <br />
       <label className=''>
         Defensa:
         <input type=""  onChange={e => setDefensa(e.target.value)} />
         {vidaError && <h5 >{vidaError}</h5>}
         {error&&<h5>{error}</h5>}
       </label>
       <br />
       <label className=''>
         velocidad:
         <input type="number"  onChange={e => setVelocidad(e.target.value)} />
         {vidaError && <h5 >{vidaError}</h5>}
         {error&&<h5>{error}</h5>}
       </label>
       <br />
       <label className=''>
         Altura:
         <input type="number" onChange={e => setAltura(e.target.value)} />
         {vidaError && <h5 >{vidaError}</h5>}
         {error&&<h5>{error}</h5>}
       </label>
       <br />
       <label className=''>
         peso:
         <input type="number" onChange={e => setPeso(e.target.value)} />
         {vidaError && <h5 >{vidaError}</h5>}
         {error&&<h5>{error}</h5>}
       </label >
       <br />
       <label className='' >
         tipo:
         <input type="text" onChange={e => setTipo(e.target.value)} />
         {status===200 && <h5>creado el pokemon</h5>}
       </label>
       
       <button className='' type="submit">Submit</button>
       
     </form>
        
      </div>

    
    </>
  )
}
