import  { useState } from "react";

import { useSelector, useDispatch } from "react-redux";
import{ getPokemonName,order}from '../Store/action'

 

export default function Buscar() {
  const dispatch = useDispatch();
  const [pokemons, setPokemons] = useState("");

  const options = useSelector((store) => store.pokemons);
  console.log(options)

  const handleInputChange = (e) => {
    setPokemons(e.target.value);
  };

  // const byTipo = (e) => {
  //   dispatch(types(e.target.value));
  // }
  
  const submit = (e) => {
    e.preventDefault();
    dispatch(getPokemonName(pokemons));
    setPokemons("");
  };

  const sele = (e) => {
    console.log(e.target.value)
    
  }

  const order = (e) => {
    console.log(e.target.value)
    
     dispatch(order(e.target.value));
   }

 
  return (
    <div >
      <form onSubmit={submit}>
        <div >
          <input
            type="text"
            
            value={pokemons}
            onChange={handleInputChange}
            placeholder="P O K E M 0 N S"
          />
          <input type="submit" value="Buscar" />
        </div>
      </form>
      <div>
        <select  name="Type" onChange={''}>
          <option value="">Tipo:</option>
          <option value="normal">normal</option>
          <option value="">Tipo:</option>
          <option value="">Tipo:</option>
          <option value="">Tipo:</option>
          <option value="">Tipo:</option>
          <option value="">Tipo:</option>
          <option value="">Tipo:</option>
          <option value="">Tipo:</option>
          <option value="">Tipo:</option>
          <option value="">Tipo:</option>
          <option value="">Tipo:</option>
          <option value="">Tipo:</option>
          <option value="">Tipo:</option>
          
        </select>
        <select name="creado" onChange={sele}>
          <option value="1">API</option>
          <option value="2">BASE-DATOS</option>
          <option value="3">ALL</option>
        </select>
        <select name="Ordenar"  onChange={order}>
          <option value="">Ordenar</option>
          <option value="a-z">A-Z</option>
          <option value="z-a">Z-A</option>
          <option value="fuerza+">MAX-FUERZA</option>
          <option value="fuerza-">MIN-FUERZA</option>
        </select>
      </div>
    </div>
  )
}
