export const getPokemon= async(dispatch)=>{ 
    return function (dispatch){
        const response =  axios.get ("https://pokeapi.co/api/v2/pokemon");
        const data =  response.results;
        console.log(data);
        dispatch({
         type: "GETPOKEMON",
