import React from 'react'
import { useEffect,useState } from "react";
import { useDispatch,useSelector} from "react-redux";
import { getPokemonName,getPokemonType,getPokemon} from "../Store/action";

export default function Filtros() {
    const pokemones=useSelector(store =>store.pokemon.pokemons)
    const teamPok=useSelector(store=>store.pokemon.team)
    console.log(teamPok)
    const allPokemon= pokemones.concat(teamPok)
    const dispatch=useDispatch();
    console.log(allPokemon)
   
    const [state, setstate] = useState([]);
    const [busque, setBusq] = useState('');
    const [refresh,setFreshen] = useState({
      Type:'',
      Ordenar:'',

    });

    const bb=(e)=>{
        const ord =e.target.value
        if(ord==='1'){
          setstate(pokemones)
        }
        if(ord==='2'){
          setstate(teamPok)
        }
        if(ord==='3'){
          setstate(allPokemon)
        }
        
      }
      const order=(e)=>{
          const ord =e.target.value
          if(ord ==='A-Z'){
              setstate(state.map(e=>e).sort(function (a, b) {
                  if (a.name > b.name) {
                    return -1;
                  }
                  if (a.name < b.name) {
                    return 1;
                  }
                  
                  return 0;
                }))
          }
          if(ord ==='Z-A'){
              setstate(state.map(e=>e).sort(function (a, b) {
                  if (a.name < b.name) {
                    return -1;
                  }
                  if (a.name > b.name) {
                    return 1;
                  }
                  // a must be equal to b
                  return 0;
                }))
          }
          if(ord ==='fuerza+'){
              
              // setstate(state.filter(pokemon => 
              //     pokemon.characters.find(stat => stat.name === 'attack' && stat.value >= 50)
              //   ))
              
              setstate(state.sort(function (a, b) {
                console.log(a)
                if (a.characters[1]['value'] < b.characters[1]['value'] ) {
                  
                  
                  return -1;
                }
                if (a.characters[1]['value']  > b.characters[1]['value'] ) {
                  return 1;
                }
                
                return 0;
              }))
              
          }
          if(ord ==='fuerza-'){
            setstate(state.sort(function (a, b) {
              console.log(a)
              if (a.characters[1]['value'] > b.characters[1]['value'] ) {
                
                
                return -1;
              }
              if (a.characters[1]['value']  < b.characters[1]['value'] ) {
                return 1;
              }
              
              return 0;
            }))
              
             
          }
      }
      const resetFilters = () => {
        
        dispatch(getPokemon())
        setstate(pokemones)
        setFreshen({
        type: '',
        order:'',
        attack: ''
        });
        setBusq('');
        }
   
      const handleInputChange = (e) => {
        e.preventDefault();
        const name=state.filter(pokemon => pokemon.name===busque)
        if(name.length===0) return <p>no se encuentra el pokemon</p>
          setstate(name)
        console.log(state.filter(pokemon => pokemon.name===busque))
        
      };
      const tipos=(e)=>{
        console.log(e.target.value)
           const  typesToFilter=[e.target.value]
           setstate(state.filter(pokemon => 
                typesToFilter.some(type => pokemon.types.includes(type))
              ))
       
  return (
    <div>
              <select name="creado" onChange={bb}>
          <option value="1">API</option>
          <option value="2">BASE-DATOS</option>
          <option value="3">ALL</option>
        </select> 
          <select  name="Type" onChange={tipos}>
          <option value="">Tipo:</option>
          <option value="normal">normal</option>
          <option value="fighting">fighting</option>
          <option value="flying">flying</option>
          <option value="poison">poison</option>
          <option value="ground">ground</option>
          <option value="rock">rock</option>
          <option value="bug">bug</option>
          <option value="ghost">ghost</option>
          <option value="steel">steel</option>
          <option value="fire">fire</option>
          <option value="water">water</option>
          <option value="grass">grass</option>
          <option value="electric">electric</option>
          <option value="psychic">psychic</option>
          <option value="ice">ice</option>
          <option value="dragon">dragon</option>
          <option value="dark">dark</option>
          <option value="fairy">fairy</option>
          <option value="unknown">unknown</option>
          <option value="shadow">shadow</option>
          
        </select> 
        <select name="Ordenar"  onChange={order}>
          <option value="">Ordenar</option>
          <option value="A-Z">Z-A</option>
          <option value="Z-A">A-Z</option>
          <option value="fuerza+">MAX-FUERZA</option>
          <option value="fuerza-">MIN-FUERZA</option>
        </select>
        <button onClick={resetFilters}>Refrescar</button>
        <form  onSubmit={handleInputChange}>
          <input type="text"  onChange={e => setBusq(e.target.value)} placeholder="       P O K E M 0 N " />
          <button className='boton-creacion' type="submit">Submit</button>
        </form>  
    </div>
  )
 }
}
