import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getPokemonBase, getPokemonName } from "../Store/action";
import './pokemon.css';
import PokemonCard from "./PokemonCard";

export function PokemonGrid({ pokemonList }) {
  const pokemones = useSelector(store => store.pokemon.pokemons);
  const teamPok = useSelector(store => store.pokemon.team);
  const allPokemon = pokemones.concat(teamPok);

  const [state, setState] = useState(pokemones);
  const [busque, setBusq] = useState('');
  const [filters, setFilters] = useState({
    type: '',
    order: '',
    attack: ''
  });
  const [dataSource, setDataSource] = useState('API');

  const dispatch = useDispatch();

  useEffect(() => {
    // Fetch data from API or DB and set it to state
    setState(pokemones);
  }, []);

  const handleTypeFilter = (e) => {
    setFilters({ ...filters, type: e.target.value });
    setState(pokemones.filter(pokemon =>
      filters.type.some(type => pokemon.types.includes(type))
    ));
  }

  const handleOrderFilter = (e) => {
    setFilters({ ...filters, order: e.target.value });
    if (filters.order === 'A-Z') {
      setState(allPokemon.map(e => e).sort((a, b) => a.name > b.name ? 1 : -1));
    } else if (filters.order === 'Z-A') {
      setState(allPokemon.map(e => e).sort((a, b) => a.name < b.name ? 1 : -1));
    } else if (filters.order === 'fuerza+') {
      setState(allPokemon.filter(pokemon =>
        pokemon.characters.find(stat => stat.name === 'attack' && stat.value >= 50)
      ));
    } else if (filters.order === 'fuerza-') {
      setState(allPokemon.filter(pokemon =>
        pokemon.characters.find(stat => stat.name === 'attack' && stat.value <= 50)
      ));
    }
  }

  const handleSearch = (e) => {
    e.preventDefault();
    setBusq(e.target.value);
    setState(getPokemonName(busque));
  }

  const handleDataSource = (e) => {
    setDataSource(e.target.value);
    if (e.target.value === '1') {
      //Fetch data from API
    } else if (e.target.value === '2') {
      //Fetch data from DB
    } else if (e.target.value === '3') {
      //Fetch data from all sources
    }
    dispatch(getPokemonBase(e.target.value));
  }
  
  const resetFilters = () => {
  setState(allPokemon);
  setFilters({
  type: '',
  order: '',
  attack: ''
  });
  setBusq('');
  }
  
  // Pagination
  const pageSize = 12;
  const [currentPage, setCurrentPage] = useState(1);
  
  function handleNextPage() {
  setCurrentPage(currentPage + 1);
  }
  
  function handlePreviousPage() {
  setCurrentPage(currentPage - 1);
  }
  
  if (state.length === 0) console.log('not data')
  
  const startIndex = (currentPage - 1) * pageSize;
  const endIndex = startIndex + pageSize;
  const pageOfPokemon = state && state.slice(startIndex, endIndex);
  
  return (
  <>
    <div className="box">
  <select name="creado" onChange={handleDataSource} value={dataSource}>
  <option value="1">API</option>
  <option value="2">BASE-DATOS</option>
  <option value="3">ALL</option>
  </select>
  
 
  <select name="Order" onChange={handleOrderFilter} value={filters.order}>
  <option value="">Orden:</option>
  <option value="A-Z">A-Z</option>
  <option value="Z-A">Z-A</option>
  <option value="fuerza+">Fuerza +</option>
  <option value="fuerza-">Fuerza -</option>
  </select>
  <input type="text" placeholder="Buscar" value={busque} onChange={handleSearch} />
  <button onClick={resetFilters}>Refrescar</button>
  { 
                  pageOfPokemon&&
                  pageOfPokemon.map((i) => <PokemonCard key={i.id}
                     
                    name={i.name}
                    tipos={i.types}
                    image={i.img}/> )              
                }

  <div className="pagination">
    <button disabled={currentPage === 1} onClick={handlePreviousPage}>Anterior</button>
    <span>PÃ¡gina {currentPage}</span>
    <button disabled={currentPage === Math.ceil(state.length / pageSize)} onClick={handleNextPage}>Siguiente</button>
  </div>
  </div>

  </>

  )

  }
