import { useDispatch } from "react-redux";
import { Link } from "react-router-dom";
import { getPokemonName } from '../Store/action';

export default function PokemonCard({ name, image, types }) {
  const dispatch = useDispatch();

  // Added a check to make sure name, image and types are defined
  if(!name || !image || !types) return null;

  return (
    <Link 
      className="caja-carta"  
      to='/detalles'
      onClick={() => dispatch(getPokemonName(name))}
    >
      <img 
        src={image} 
        alt={name}  
        width="90"
        height="70" 
      />
      <div className='caja-de-letras'>
        <p>
          <p className='nombres'>Nombre:</p>
          {name}
        </p>
        <p>
          <p className='nombres'>Tipos:</p>
          {types[0]}
        </p>
      </div>
    </Link>
  )
}
