import axios from 'axios';

export const GETPOKEMON='GETPOKEMON'
export const GETPOKEMONAME='GETPOKEMONAME'
export const GETPOKEMONID='GETPOKEMONID'
export const GETPOKEMONTYPE='GETPOKEMONTYPE'


export const getPokemon =() => async (dispatch) => {
    try {
      const response = await axios.get("http://localhost:3001/pokemons/");
      
      
      dispatch({
        type: "GETPOKEMON",
        payload: response,
      });
    } catch (error) {
      console.log(error);
    }
  };
  

export const getPokemonName=(name) => async (dispatch) => {
    try {
      const response = await axios.get(`https://pokeapi.co/api/v2/pokemon/${name}`);
      const data = response.data.results;
      console.log(data);
      dispatch({
        type: "GETPOKEMONAME",
        payload: data,
      });
    } catch (error) {
      console.log(error);
    }
  };
export const getPokemonId=(id) => async (dispatch)=>{ 
    try {
        const response = await axios.get(`https://pokeapi.co/api/v2/pokemon${id}`);
        const data = response.data.results;
        console.log(data);
        dispatch({
         type: "GETPOKEMONID",
         payload: data,
       });
    }catch (error){
        console.log(error)
    }
   
  }
export const getPokemonType=async(dispatch)=>{ 
    try{
    const response = await axios.get(`https://pokeapi.co/api/v2/types`);
    const data = response.data.results;
    console.log(data);
    dispatch({
     type: "GETPOKEMONID",
     payload: data,
   });
}catch (error){
    console.log(error)
}
}
