import React,{useState} from 'react'
import { useDispatch} from "react-redux";
import { getPokemonName } from '../../Store/action';
import './search.css';

export default function Search() {
   
    const [busque, setBusq] = useState('');
    const dispatch=useDispatch();
    const handleInputChange = (e) => {
        e.preventDefault();
     
        dispatch(getPokemonName(busque))
      };
     

  return (
    <form className='search'  onSubmit={handleInputChange}>
    <input type="text" className='imput'  onChange={e => setBusq(e.target.value)} placeholder="       P O K E M 0 N " />
    <button className="boton" type="submit">Search</button>
  </form>
  )
}
