import React,{useState,useEffect} from 'react'
import { useSelector} from 'react-redux'
import { Link } from 'react-router-dom';
import './details.css';
export default function Details() {
   
    const pokemones=useSelector(store =>store.pokemon.pokemonName)
    const [state, setState] = useState([]);
useEffect(() =>{
 setState(pokemones)
},[pokemones])
        
     
    console.log(state)
    if(state[0]){
        return (
            <div className='caja-central'>
                <div className='caja-send'>
                <img className='immg' src={state[0].img} alt={state[0].name}width="200" height="200" />
                <p>_____________________</p>
                <section>
                  <h1>{state[0]?.name}</h1>
           
                 <ul>
                    {
                        state[0]?.Estadísticas.map(e=><li key={e}>{e}</li>)
                    }
                 </ul>
                 <ul>
               
                 </ul>
                </section>
                <Link to='/pokemon'onChange={()=>{setState([])}}>Atras</Link>
                </div>
               
                
            </div>
          )

    }else{
        return (
            <div>
                <h1>no date</h1>
            </div>
          )
    }

}
