import { GETPOKEMON } from "./action";
   const initialState = {
    pokemons:[]
    
 
   };;
     export default function reducer (state=initialState,action){
        switch(action.type){
            case GETPOKEMON:
                return{
                  ...state,
                  pokemons: action.payload,
                };
                   
            default:
              return state;
             
        }
     }
