import { useForm } from "react-hook-form"
import { useDispatch } from "react-redux";
import{getPokemonName}from '../Store/action'

 

export default function Buscar() {
  const dispatch = useDispatch();
  const [pokemons, setPokemons] = useState("");

  const options = useSelector((store) => store.types);
  const button = style.button;

  const handleInputChange = (e) => {
    setPokemons(e.target.value);
  };

  const byTipo = (e) => {
    dispatch(type(e.target.value));
  }
  
  const submit = (e) => {
    e.preventDefault();
    dispatch(getByName(pokemons));
    setPokemons("");
  };

  const creadoBy = (e) => {
    dispatch(filters(e.target.value))
  }

  const orderBy = (e) => {
    dispatch(order(e.target.value));
  }

 
  return (
    <div>
        <form onSubmit={handleSubmit(onSubmit)}>
        <select {...register('base')}>
         <option value='1'>api</option>
         <option value='2'>baseData</option>
         <option value='3'>all</option> 
      </select>
       
    
        <input type='text' {...register('nombre',{
         
         maxLength:10,
         
       })}  />  
       {errors.buscar?.type==="required"&& <p>solo 10 caracteres</p>}
       <label>Tipos</label>
       <select {...register('tipos')}>
         <option value='No'>none</option>
         <option value='normal'>normal</option>
         <option value='fighting'>fighting</option>
         <option value='flying'>flying</option>
         <option value='poison'>poison</option>
         <option value='ground'>ground</option>
         <option value='rock'>rock</option>
         <option value='bug'>bug</option>
         <option value='ghost'>ghost</option>
         <option value='steel'>steel</option>
         <option value='fire'>fire</option>
         <option value='water'>water</option>
         <option value='grass'>grass</option>
         <option value='electric'>electric</option>
         <option value='psychic'>psychic</option>
         <option value='ice'>ice</option>
         <option value='dragon'>dragon</option>
         <option value='dark'>dark</option>
         <option value='fairy'>fairy</option>
         <option value='unknown'>unknown</option>
         <option value='darshadowk'>darshadowk</option>
      </select>
      <label>orden</label>
      <select {...register('tipos')}>
         <option value='No'>none</option>
         <option value='A-Z'>A-Z</option>
         <option value='Z-A'>Z-A</option>
         <option value='MAX-ATTACK'>MAX-ATTACK</option>
         <option value='MIN-ATTACK'>MIN-ATTACK</option>
      </select>
       
       
       <input type="submit" value="Buscar" />
       <input type="submit" value="Refress" />
        </form>
      

     
   
     
       
        
    </div>
  )
}
