import React from 'react'
import { useDispatch} from "react-redux";
import { Link } from 'react-router-dom'
import { getPokemonId } from '../../Store/action';
import './card.css';

export default function Card(props) {
  const dispatch=useDispatch()
  const attack=stats[1]
  return (
    <Link className='caja-cartas' to='/details' onClick={() => dispatch(getPokemonId(pokemon.id))}>
      <img className='img' src={pokemon.img || pokemon.imag || pokemon.url} alt={pokemon.name} width="100" height="100" />
      <div className='letras'>
        <p>Name</p>
        <h5>{pokemon.name}</h5>
        <p>Attack</p>
        <h5>{attack.value}</h5>
        <p>Id</p>
        <h5>{pokemon.id}</h5>
        <p>Types</p>
        <h5>{pokemon.types.map((type) => <li key={type}>{type}</li>)}</h5>
        <p>Source</p>
        <h5>{pokemon.source}</h5>
      </div>
    </Link>
  );
}
