import React,{useState,useEffect} from 'react'
import { useSelector} from 'react-redux'
import { Link } from 'react-router-dom';
export default function Details() {
    
    const pokemones=useSelector(store =>store.pokemon.pokemonName)
  useEffect()
        
     
    console.log(pokemones[0])
    if(pokemones[0]){
        return (
            <div>
                <img src={pokemones[0].img} alt={pokemones[0].name}width="200" height="200" />
                <section>
                  <h1>{pokemones[0].name}</h1>
                </section>
                <Link to='/pokemon'>Atras</Link>
                
            </div>
          )

    }else{
        return (
            <div>
                <h1>no date</h1>
            </div>
          )
    }

}
