import React, { useState, useEffect } from "react";
import { useSelector } from "react-redux";
import { Link } from "react-router-dom";
import "./details.css";

export default function Details() {
  const pokemones = useSelector((store) => store.pokemon.pokemonId);
  const [state, setState] = useState();

  useEffect(() => {
    setState(pokemones);
  }, [pokemones]);

  if (state) {
    const { name, img, stats } = state[0];

    return (
      <div className="caja-central">
        <div className="caja-send">
          <img
            className="immg"
            src={img || state[0]?.imag}
            alt={name || state[0]?.name}
            width="200"
            height="200"
          />
          <p>_____________________</p>
          <section>
            <h1>{name || state[0]?.name}</h1>

            <ul>
              {stats
                ? stats.map((stat) => (
                    <>
                      <li>{stat.name}</li> <li>{stat.value}</li>
                    </>
                  ))
                : [
                    <>
                      <li>hp: {state[0]?.stats[0]?.hp}</li>
                      <li>speed: {state[0]?.stats[0]?.speed}</li>
                      <li>life: {state[0]?.stats[0]?.life}</li>
                    </>,
                  ]}
            </ul>
          </section>
          <Link to="/pokemon">Atras</Link>
        </div>
      </div>
    );
  } else {
    return (
      <div>
        <br />
        <h1>no date</h1>
      </div>
    );
  }
}
