import { useEffect } from "react";
import { useDispatch,useSelector} from "react-redux";
import { getPokemon,  } from "../Store/action";


import './pokemon.css';
import PokemonCard from "./PokemonCard";


export function PokemonGrid(){
    const pokemones=useSelector(store =>store.pokemon.pokemons)
    const dispatch=useDispatch();
    
    
    
    useEffect(() => {
        
        dispatch(getPokemon().map(e=>e).sort(function (a, b) {
            if (a.name > b.name) {
              return 1;
            }
            if (a.name < b.name) {
              return -1;
            }
            // a must be equal to b
            return 0;
          }))
       
      },[]);
    
 
    //if (sort === "highPrice") return encontrados.sort((a, b) => b.price - a.price);
        console.log(pokemones)


    return(
        <div className="box">   
           
            <ul className="box">
                {
                    pokemones
                     && pokemones.map((i) => <PokemonCard key={i.id}
                     
                    name={i.name}
                    tipos={i.types}
                    image={i.img}/> )
                            
                }
            </ul>
        </div>
    );
};
