import {useEffect,useState} from 'react'
import { useSelector,useDispatch } from 'react-redux'
import { getPokemonFilter } from '../../Store/action';


export default function Filter() {
  const dispatch = useDispatch();
  const [filters, setFilters] = useState('res');
  
  useEffect(() => {
    dispatch(getPokemonFilter(filters));
  }, [filters]);

  const order = (e) => {
    setFilters(e.target.value);
  };

  return (
    <div>
      <select name="Ordenar" onChange={order} value={filters}>
        <option value="res">Ordenar</option>
        <option value="Z-A">Z-A</option>
        <option value="A-Z">A-Z</option>
        
        <option value="fuerza+">MAX-FUERZA</option>
        <option value="fuerza-">MIN-FUERZA</option>
      </select>
    </div>
  );
}
