export const getPokemon= async()=>{ 
 const response = await fetch("https://kevindex.herokuapp.com/types");
console.log(response);
const data = await response.json();
console.log(data);
dispatch({
  type: "GET_TYPE",
  payload: data,
});
};

export const getPokemonName=()=>{ 
    return{
        type:GETPOKEMONAME
    } 
}
