import axios from 'axios';

export const GETPOKEMON='GETPOKEMON'
export const GETPOKEMONAME='GETPOKEMONAME'
export const GETPOKEMONID='GETPOKEMONID'
export const GETPOKEMONTYPE='GETPOKEMONTYPE'


export const getPokemon =() => async (dispatch,getState) => {
    try {
      
      const response = await axios.get('http://localhost:3001/pokemons/');
      const pokemons = response.data.results;
      const pokemonsData = await Promise.all(pokemons.map(async pokemon => {
        const res = await axios.get(pokemon.url);
        return {
          id: res.data.id,
          name: res.data.name,
          height: res.data.height,
          weight: res.data.weight,
          types: res.data.types.map(type => type.type.name),
          img: res.data.sprites.other.home
        }
      }));
      console.log(pokemonsData);
      
      dispatch({
        type: "GETPOKEMON",
        payload: pokemonsData,
      });
    } catch (error) {
      console.log(error);
    }
  };
  

export const getPokemonName=(name) => async (dispatch) => {
    try {
      const response = await axios.get(`http://localhost:3001/pokemons?name=${name}`);
      const data = response.data;
      console.log(data);
      dispatch({
        type: "GETPOKEMONAME",
        payload: data,
      });
    } catch (error) {
      console.log(error);
    }
  };
export const getPokemonId=(id) => async (dispatch)=>{ 
    try {
        const response = await axios.get(`http://localhost:3001/pokemons?by=${id}`);
        const data = response.data.results;
        console.log(data);
        dispatch({
         type: "GETPOKEMONID",
         payload: data,
       });
    }catch (error){
        console.log(error)
    }
   
  }
export const getPokemonType=async(dispatch)=>{ 
    try{
    const response = await axios.get(`http://localhost:3001/types/`);
    const data = response.data.results;
    console.log(data);
    dispatch({
     type: "GETPOKEMONID",
     payload: data,
   });
}catch (error){
    console.log(error)
}
}
