import { GETPOKEMON,GETPOKEMONAME,GETPOKEMONID,GETPOKEMONTYPE } from "./action";
   const initialState = {
    pokemons: [],
    types: [],
 
   };;
     export default function reducer (state=initialState,action){
        switch(action.type){
            case GETPOKEMON:
                return{
                  ...state,
                  pokemons: action.payload,
                };
                

            case GETPOKEMONAME:
              return{
                ...state,
                pokemons: action.payload
              }

            case GETPOKEMONID:
              return{
                ...state,
                pokemons: action.payload
              }

            case GETPOKEMONTYPE:
              return{
                ...state,
                types: action.payload,
              };
               
            default:
              return state;
             
        }
     }
