import  { useState } from "react";

import { useSelector, useDispatch } from "react-redux";
import{ getPokemonName,filters}from '../Store/action'

 

export default function Buscar() {
  const dispatch = useDispatch();
  const [pokemons, setPokemons] = useState("");

  const options = useSelector((store) => store.types);
 

  const handleInputChange = (e) => {
    setPokemons(e.target.value);
  };

  // const byTipo = (e) => {
  //   dispatch(types(e.target.value));
  // }
  
  const submit = (e) => {
    e.preventDefault();
    dispatch(getPokemonName(pokemons));
    setPokemons("");
  };

  const creadoBy = (e) => {
    dispatch(filters(e.target.value))
  }

  // const orderBy = (e) => {
  //   dispatch(order(e.target.value));
  // }

 
  return (
    <div >
      <form onSubmit={submit}>
        <div >
          <input
            type="text"
            id="searchterm"
            value={pokemons}
            onChange={handleInputChange}
            placeholder="P O K E M 0 N S"
          />
          <input type="submit" value="Buscar" />
        </div>
      </form>
      <div>
        <select  name="Type" onChange={''}>
          <option value="">Tipo:</option>
          {options?.map((p) => (
            <option value={p.name} key={p.slot}>
              {p.name}
            </option>
          ))}
        </select>
        <select name="creado" onChange={creadoBy}>
          <option value="1">API:</option>
          <option value="2">BASEDATOS</option>
          <option value="2">Fandom</option>
        </select>
        <select name="Ordenar"  onChange={''}>
          <option value="">Ordenar por:</option>
          <option value="a-z">A-Z</option>
          <option value="z-a">Z-A</option>
          <option value="fuerza+">Fuerza+</option>
          <option value="fuerza-">Fuerza-</option>
        </select>
      </div>
    </div>
  )
}
