import './addPokemon.css'
import { getPokemonBase} from "../Store/action";
import React, { useState } from 'react';
import axios from 'axios';
import { useEffect } from "react";
import { useDispatch} from "react-redux";

function AddPokemon() {
 
  const dispatch=useDispatch();
  const [name, setName] = useState('');
  const [vida, setVida] = useState(0);
  const [fuerza, setFuerza] = useState(0);
  const [defensa, setDefensa] = useState(0);
  const [velocidad, setVelocidad] = useState(0);
  const [altura, setAltura] = useState(0);
  const [peso, setPeso] = useState(0);
  const [tipos, setTipo] = useState('');
  useEffect(() => {
    
  
      
      dispatch(getPokemonBase(2))
     
    },[]);
  
 

  const handleSubmit = (event) => {
    event.preventDefault();
    axios.post('http://localhost:3001/pokemons/', {
     name,
     vida,
     fuerza,
     defensa,
     velocidad,
     altura,
     peso,
     tipos
    })
    .then(response => console.log(response))
    .catch(error => console.error(error));
  }

  return (
  <>
       
       <br />
       <br />
      <div className='caja-de-creacion'>
        <h1 className='titulo-creacion'>Crear Pokemon</h1>
      <form className='crear-pokemon' onSubmit={handleSubmit}>
       
       <label className='name'>
         Name:
         <input type="text" onChange={e => setName(e.target.value)} />
       </label>
       <br />
       <label className='name'>
         Vida:
         <input type="number"  onChange={e => setVida(e.target.value)} />
       </label>
       <br />
       <label className='name'>
         Fuerza:
         <input type="number"  onChange={e => setFuerza(e.target.value)} />
       </label>
       <br />
       <label className='name'>
         Defensa:
         <input type="number"  onChange={e => setDefensa(e.target.value)} />
       </label>
       <br />
       <label className='name'>
         velocidad:
         <input type="number"  onChange={e => setVelocidad(e.target.value)} />
       </label>
       <br />
       <label className='name'>
         Altura:
         <input type="number" onChange={e => setAltura(e.target.value)} />
       </label>
       <br />
       <label className='name'>
         peso:
         <input type="number" onChange={e => setPeso(e.target.value)} />
       </label >
       <br />
       <label className='name' >
         tipo:
         <input type="text" onChange={e => setTipo(e.target.value)} />
       </label>
       
       <button className='boton-creacion' type="submit">Submit</button>
     </form>
        
      </div>

    
    </>
  );
}

export default AddPokemon;
