export default function About({ pokemonList }) {
  const pageSize = 12; // Número de Pokémon a mostrar por página
  const [currentPage, setCurrentPage] = useState(1); // Página actual

  function handleNextPage() {
    setCurrentPage(currentPage + 1);
  }

  function handlePreviousPage() {
    setCurrentPage(currentPage - 1);
  }

  const startIndex = (currentPage - 1) * pageSize;
  const endIndex = startIndex + pageSize;
  const pageOfPokemon = pokemonList.slice(startIndex, endIndex);

  return (
    <div>
      {pageOfPokemon.map((pokemon) => (
        <PokemonCard key={pokemon.id} pokemon={pokemon} />
      ))}
      <button onClick={handlePreviousPage} disabled={currentPage === 1}>
        Anterior
      </button>
      <button onClick={handleNextPage} disabled={endIndex >= pokemonList.length}>
        Siguiente
      </button>
    </div>
  );
}
