import { useEffect } from "react";
import { useDispatch,useSelector} from "react-redux";
import { getPokemon, getPokemonType,buscarTipos  } from "../Store/action";


import './pokemon.css';
import PokemonCard from "./PokemonCard";


export function PokemonGrid(){
    const pokemones=useSelector(store =>store.pokemon.pokemons)
    const type=useSelector(store =>store.pokemon)
    
    const dispatch=useDispatch();
    
    
    
    useEffect(() => {
        
        dispatch(getPokemon())
        dispatch(getPokemonType())
        dispatch(buscarTipos)
        
       
      },[]);
    
 
    //if (sort === "highPrice") return encontrados.sort((a, b) => b.price - a.price);
        console.log(pokemones)
        //console.log(type)


    return(
        <div className="box">   
           
            <ul className="box">
                {
                    pokemones
                     && pokemones.map((i) => <PokemonCard key={i.id}
                     
                    name={i.name}
                    tipos={i.types}
                    image={i.img}/> )
                            
                }
            </ul>
        </div>
    );
};
