import { useSelector} from "react-redux";
import { useState } from "react";

import'./detalles.css'




export default function PokemonDetall() {
  const imag="https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/versions/generation-v/black-white/animated/25.gif"
   const err = []
   const [pokemon,setPokemon]=useState()
   const pokemones = useSelector(store => store.pokemon.pokemons)
   console.log(pokemones.id)
   if(pokemones.id){
    return (
      <>
      <br />
      <br />
      <br />
      <div>
        {(
          <div className="caja-pricipal">
            <div className="caja-image">
              <img src={imag} width="200" height="70" />
            </div>
            <div className="caja-letras">
              <h2>{pokemones.name}</h2>
              <h3>ID : {pokemones.id}</h3>
              <p>hp {pokemones.fuerza}</p>
              <p>vida {pokemones.vida}</p>
            <p>Attak {pokemones.ataque}</p>
            <p>Defense {pokemones.defensa}</p>
            <p>Speed {pokemones.velocidad}</p>
            <p>Altura {pokemones.altura}</p>
            <p>peso {pokemones.peso}</p>
            
              
         
            </div>
          </div>
        )}
      </div>
    </>
  
      
    )

   }
   if(pokemones && pokemones.length > 0) {
    console.log(pokemones)
    if (pokemones[0]) {
      console.log(pokemones)
      
      if(pokemones[0].stats){
        console.log(pokemones[0].stats)
        const stat = pokemones[0].stats
        if (stat.length > 0) {
          for (const iterator of stat) {
            err.push(iterator.value)
          }
        }
      }else{
        
     
      console.log("pokemones[0].stats is not defined")
      }
    }else{
      
      console.log("pokemones[0] is not defined")
    }
  }else{
    
    console.log("pokemones is not defined or has a length of 0")
  }
  
  console.log(err)
  return (
    <>
    <br />
    <br />
    <br />
    <div>
      {pokemones.length > 0 && (
        <div className="caja-pricipal">
          <div className="caja-image">
            <img src={pokemones[0].img} width="200" height="200" />
          </div>
          <div className="caja-letras">
            <h2>{pokemones[0].name}</h2>
            <h3>ID : {pokemones[0].id}</h3>
            
            <p>hp {err[0]}</p>
            <p>attak {err[1]}</p>
            <p>defense{err[1]}</p>
            <p>attak-special {err[1]}</p>
            <p>attak-defense {err[1]}</p>
            <p>attak speed {err[1]}</p>
          </div>
        </div>
      )}
    </div>
  </>

    
  )
}
