import axios from 'axios';

export const GETPOKEMON='GETPOKEMON'

export const GETPOKEMONAME='GETPOKEMONAME'
export const GETPOKEMONID='GETPOKEMONID'
export const GETPOKEMONTYPE='GETPOKEMONTYPE'


export const getPokemon= async()=>{ 
    return function (dispatch) {
        return axios("https://pokeapi.co/api/v2/pokemon")
          .then(response => response.json())
          .then(json => {
          dispatch({ type: GETPOKEMON, payload: json });
          })
          .catch((error)=>{
            console.log(error);
            })
    }
}
export const getPokemonName=async(dispatch)=>{ 
    const response = await axios.get("https://pokeapi.co/api/v2/pokemon");
    const data = await response.results;
    console.log(data);
    dispatch({
     type: "GETPOKEMONAME",
     payload: data,
   });
}
export const getPokemonId=async(dispatch)=>{ 
    const response = await axios.get("https://pokeapi.co/api/v2/pokemon");
    const data = await response.results;
    console.log(data);
    dispatch({
     type: "GETPOKEMONID",
     payload: data,
   });
}
export const getPokemonType=async(dispatch)=>{ 
    const response = await axios.get("https://pokeapi.co/api/v2/pokemon");
    const data = await response.results;
    console.log(data);
    dispatch({
     type: "GETPOKEMONTYPE",
     payload: data,
   });
}
