import { useEffect } from "react";
import { useDispatch,useSelector} from "react-redux";
import { getPokemon, getPokemonType,buscarTipos  } from "../Store/action";


import './pokemon.css';
import PokemonCard from "./PokemonCard";


export function PokemonGrid(){
    const pokemones=useSelector(store =>store.pokemon.pokemons)
    const type=useSelector(store =>store.pokemon)
    
    const dispatch=useDispatch();
    
    
    
    useEffect(() => {
        
        dispatch(getPokemon())
        dispatch(getPokemonType())
        
        
       
      },[]);
    
 

        console.log(pokemones)
        console.log(type)
      

    return(
        <div className="box">   
           <button onClick={()=>{
            const a=0+b
            const b=50+b
            dispatch(getPokemon(a,b))}}>siguiente</button>
            
            <ul className="box">
                {
                    pokemones
                     && pokemones.map((i) => <PokemonCard key={i.id}
                     
                    name={i.name}
                    tipos={i.types}
                    image={i.img}/> )
                            
                }
            </ul>
        </div>
    );
};
