import { useEffect,useState } from "react";
import { useDispatch,useSelector} from "react-redux";
import { getPokemon, getPokemonType,buscarTipos  } from "../Store/action";


import './pokemon.css';
import PokemonCard from "./PokemonCard";


export function PokemonGrid(){
    const pokemones=useSelector(store =>store.pokemon.pokemons)
    const [state, setstate] = useState(pokemones);
    console.log(state)
    
    // console.log(pokemones.filter(pokemon => 
    //     typesToFilter.some(type => pokemon.types.includes(type))
    //   ),)
    const type=useSelector(store =>store.pokemon)
    
    const dispatch=useDispatch();
    
   // const [a, setStateA] = useState(0);
    //const [b, setStateB] = useState(0);
    
    useEffect(() => {
        
        dispatch(getPokemon(0,50))
        dispatch(getPokemonType())
        setstate(pokemones)
        
       
      },[]);
    
    
        console.log(state)
        console.log(type)
        console.log(state)

    return(
        <div className="box">  
          <select  name="Type" onChange={(e)=>{
           console.log(e.target.value)
           const  typesToFilter=[e.target.value]
           setstate(state.filter(pokemon => 
                typesToFilter.some(type => pokemon.types.includes(type))
              ))
          }}>
          <option value="">Tipo:</option>
          <option value="normal">normal</option>
          <option value="fighting">fighting</option>
          <option value="flying">flying</option>
          <option value="poison">poison</option>
          <option value="ground">ground</option>
          <option value="rock">rock</option>
          <option value="bug">bug</option>
          <option value="ghost">ghost</option>
          <option value="steel">steel</option>
          <option value="fire">fire</option>
          <option value="water">water</option>
          <option value="grass">grass</option>
          <option value="electric">electric</option>
          <option value="psychic">psychic</option>
          <option value="ice">ice</option>
          <option value="dragon">dragon</option>
          <option value="dark">dark</option>
          <option value="fairy">fairy</option>
          <option value="unknown">unknown</option>
          <option value="shadow">shadow</option>
          
        </select> 
           <button onClick={()=>{
            setstate(pokemones)
            }}>Ver pokemones</button>
            <button onClick={()=>{
            dispatch(getPokemon(0,50))  
            }}>atras</button>
           
            <ul className="box">
                { 
                  
                     state&&
                     state.map((i) => <PokemonCard key={i.id}
                     
                    name={i.name}
                    tipos={i.types}
                    image={i.img}/> )
                            
                }
            </ul>
        </div>
    );
};
