            <ul className="box">
                {
                    pokemones
                     && pokemones.map((i) => <PokemonCard key={i.id}
                     
                    name={i.name}
                    tipos={i.types}
                    image={i.img}/> )
                            
                }
            </ul>
        </div>
    );
};
