import React from 'react'
import { useDispatch} from "react-redux";
import { Link } from 'react-router-dom'
import { getPokemonId } from '../../Store/action';
import './card.css';

export default function Card(props) {
  const dispatch=useDispatch()
  const attack = props?.stats[1];
  return (
    <Link className='caja-cartas' to='/details' onClick={() => dispatch(getPokemonId(props.id))}>
      <img className='img' src={props.img || props.imag || props.url} alt={props.name} width="100" height="100" />
      <div className='letras'>
        <p>Name</p>
        <h5>{props.name}</h5>
        <p>Attack</p>
        <h5>{attack.value||props.hp}</h5>
        <p>Id</p>
        <h5>{props.id}</h5>
        <p>Types</p>
        <h5>{props.types?.map((type) => <li key={type}>{type}</li>)||props.type}</h5>
       
      </div>
    </Link>
  );
}
