import React,{useState,useEffect} from 'react'
import { useSelector} from 'react-redux'
import { Link } from 'react-router-dom';
import './details.css';
export default function Details() {
   
    const pokemones = useSelector(store => store.pokemon.pokemonId);
    const [state, setState] = useState();
    
    useEffect(() => {
      setState(pokemones);
    }, [pokemones]);
    
    if (state && state[0]?.stats) {
      return (
        <div className='caja-central'>
          <div className='caja-send'>
            <img
              className='immg'
              src={state.imag || state.img || state[0]?.img}
              alt={state?.name || state[0]?.name}
              width='200'
              height='200'
            />
            <p>_____________________</p>
            <section>
              <h1>{state?.name || state[0]?.name}</h1>
    
              <ul>
                {state?.stats &&
                  state.stats.map((e) => (
                    <>
                      <li>{e.name}</li> <li>{e.value}</li>
                    </>
                  ))}
                {!state?.stats &&
                  state[0]?.stats?.map((e) => (
                    <>
                      <li>{e.value}</li>
                    </>
                  ))}
              </ul>
              <ul></ul>
            </section>
            <Link to='/pokemon' onClick={() => setState('')}>
              Atras
            </Link>
          </div>
        </div>
      );
    } else {
      return (
        <div>
          <br />
          <h1>no date</h1>
        </div>
      );
    }
}
