import { useSelector} from "react-redux";
import {useState } from "react";
import'./detalles.css'




export default function PokemonDetall() {
   const err = []
   const [state, setState] = useState([]);
   //const pokemones = useSelector(store => store.pokemon.pokemons)
   const allpokemon =useSelector(store =>store.pokemon.team)
   if(allpokemon)
   
   if(allpokemon && allpokemon.length > 0) {
   
    if (allpokemon[0]) {
      
      if(allpokemon[0].stats){
        console.log(allpokemon[0].stats)
        const stat = allpokemon[0].stats
        if (stat.length > 0) {
          for (const iterator of stat) {
            err.push(iterator.value)
          }
        }
      }else{
     
      console.log("pokemones[0].stats is not defined")
      }
    }else{
      
      console.log("pokemones[0] is not defined")
    }
  }else{
    
    console.log("pokemones is not defined or has a length of 0")
  }
  
  console.log(err)
  return (
    <>
    <br />
    <br />
    <br />
    <div>
      {allpokemon.length > 0 && (
        <div className="caja-pricipal">
          <div className="caja-image">
            <img src={allpokemon[0].img} width="200" height="200" />
          </div>
          <div className="caja-letras">
            <h2>{allpokemon[0].name}</h2>
            <h3>ID : {allpokemon[0].id}</h3>
            
            <p>hp {err[0]}</p>
            <p>attak {err[1]}</p>
            <p>defense{err[1]}</p>
            <p>attak-special {err[1]}</p>
            <p>attak-defense {err[1]}</p>
            <p>attak speed {err[1]}</p>
          </div>
        </div>
      )}
    </div>
  </>

    
  )
}
