import {  useSelector } from "react-redux";


import './pokemon.css';
import PokemonCard from "./PokemonCard";


export function PokemonGrid(){
    if(useSelector(store=>store.pokemons[0])){
        const pokemones=useSelector(store=>store.pokemons[0])
        return(
            <div className="box">   
               
                <ul className="box">
                    {
                        pokemones.map((i) => <PokemonCard 
                        src={i.image} 
                        name={i.name}
                        tipo={i.tipo}
                        image={i.imag}/> )
                                
                    }
                </ul>
            </div>
        );
    }
   
    
    
    
   

    return(
        <div className="box">   
           
            <ul className="box">
                {
                    pokemones.map((i) => <PokemonCard 
                    src={i.image} 
                    name={i.name}
                    tipo={i.tipo}
                    image={i.imag}/> )
                            
                }
            </ul>
        </div>
    );
};
