import { useEffect,useState } from "react";
import { useDispatch,useSelector} from "react-redux";
import Filtros from "./Filtros";
// import { getPokemonName,getPokemonType,getPokemon} from "../Store/action";


import './pokemon.css';
import PokemonCard from "./PokemonCard";


export function PokemonGrid({ pokemonList }){
  //llamamos a la base de datos de la api , la creada y por ultimo a todos
    const pokemones=useSelector(store =>store.pokemon.pokemons)
    

   
   

   
    const [state, setstate] = useState([]);

    
    // setiamos con los pokemones de la base de datos api 
    useEffect(() => {
        
     
        setstate(pokemones)
        
       
      },[]);

     
  // paginado
   const pageSize = 12;
   const [currentPage, setCurrentPage] = useState(1);
        
    function handleNextPage() {
    setCurrentPage(currentPage + 1);
   }

    function handlePreviousPage() {
    setCurrentPage(currentPage - 1);
   } 
   
    
      const startIndex = (currentPage - 1) * pageSize;
      const endIndex = startIndex + pageSize;
      console.log(state)
      if(state.length===0)console.log('not data')
      const pageOfPokemon  = state&&state.slice(startIndex, endIndex);  
      console.log(pageOfPokemon)
   
  
   
      

     

    return(
        <div className="box"> 

         <Filtros />
            <ul className="box">
                { 
                  pageOfPokemon&&
                  pageOfPokemon.map((i) => <PokemonCard key={i.id}
                     
                    name={i.name}
                    tipos={i.types}
                    image={i.img}/> )              
                }
            </ul>
            <div>
            <button onClick={handlePreviousPage} disabled={currentPage === 1}>
             Anterior
            </button>
            <button onClick={handleNextPage} disabled={endIndex >= state.length}>
             Siguiente
            </button> 
           </div>
          
     </div>
    );
};
