import { useForm } from "react-hook-form"
import { useDispatch } from "react-redux";
import{getPokemonName}from '../Store/action'

 

export default function Buscar() {
  const dispatch = useDispatch();
  const [pokemons, setPokemons] = useState("");

  const options = useSelector((store) => store.types);
  const button = style.button;

  const handleInputChange = (e) => {
    setPokemons(e.target.value);
  };

  const byTipo = (e) => {
    dispatch(type(e.target.value));
  }
  
  const submit = (e) => {
    e.preventDefault();
    dispatch(getByName(pokemons));
    setPokemons("");
  };

  const creadoBy = (e) => {
    dispatch(filters(e.target.value))
  }

  const orderBy = (e) => {
    dispatch(order(e.target.value));
  }

 
  return (
    <div className={style.container}>
      <form onSubmit={submit}>
        <div >
          <input
            type="text"
            id="searchterm"
            value={pokemons}
            onChange={handleInputChange}
            placeholder="Encuentra tu pokemon..."
          />
          <input className={button} type="submit" value="Find!" />
        </div>
      </form>
      <div>
        <select  name="Type" onChange={byTipo}>
          <option value="">Tipo:</option>
          {options?.map((p) => (
            <option value={p.name} key={p.slot}>
              {p.name}
            </option>
          ))}
        </select>
        <select name="creado" onChange={creadoBy}>
          <option value="0">Creado por:</option>
          <option value="1">API</option>
          <option value="2">Fandom</option>
        </select>
        <select name="Ordenar"  onChange={orderBy}>
          <option value="">Ordenar por:</option>
          <option value="a-z">A-Z</option>
          <option value="z-a">Z-A</option>
          <option value="fuerza+">Fuerza+</option>
          <option value="fuerza-">Fuerza-</option>
        </select>
      </div>
    </div>
  )
}
