import './card.css'
import { useState,use } from 'react';
import { useDispatch} from "react-redux";
import { Link } from "react-router-dom";
import { getPokemonName } from '../Store/action';

export default function PokemonCard(props) {
  const [state, setstate] = useState();
  const dispatch=useDispatch()
  return (
    <Link onClick={()=>dispatch(getPokemonName(props.name))} className="caja-carta"  to='/detalles'> 
    
   
      
     
     <img src={props.image} alt={props.name}  width="90"
       height="70" />


      <div className='caja-de-letras'>
      <p><p className='nombres'>Nombre:</p>{props.name}</p>
      <p><p className='nombres'>Tipos:</p>  {props&&props.types}</p>
      </div>
     
      
   
    </Link>

  )
}
