import { useState,useEffect } from 'react';
import { useDispatch} from "react-redux";
import { Link } from "react-router-dom";
import { getPokemonName } from '../Store/action';

export default function PokemonCard(props) {
  const [state, setState] = useState([]);
  useEffect(() => {
    setState(props);
  }, []);
  const dispatch=useDispatch()
  return (
    <Link onClick={()=>dispatch(getPokemonName(state.name))} className="caja-carta"  to='/detalles'> 
    
   
      
     
     <img src={state.image} alt={state.name}  width="90"
       height="70" />


      <div className='caja-de-letras'>
      <p><p className='nombres'>Nombre:</p>{state.name}</p>
