import React from 'react'
import { useDispatch} from "react-redux";
import { Link } from 'react-router-dom'
import { getPokemonName } from '../../Store/action';
import './card.css';

export default function Card({id,name,img,types,stats}) {
  const dispatch=useDispatch()
  const attack=stats[1]
 
   
  return (
    <Link className='caja-cartas' to='/details' onClick={()=>dispatch(getPokemonName(name))}>
      <img className='img' src={img} alt={name} width="90"
       height="70"/>
       <div className='letras'>
       <p>Name</p>
      <h5>{name}</h5>
      <p>Attack</p>
      <h5>{attack}</h5>
      <p>Id</p>
      <h5>{id}</h5>
      <p>Types</p>
      <h5>{types?.map(e=><li>{e}</li>)}</h5>
      </div>
        
        
       
    
     
    </Link>
  )
}
