import React, { useEffect, useState } from "react";
import { useDispatch,useSelector} from "react-redux";
import { getPokemon,  } from "../Store/action";
import './pokemon.css';
import PokemonCard from "./PokemonCard";


export function PokemonGrid(){
    const [pokemones, setPokemon] = useState([]);
    setPokemon(useSelector(store =>store.pokemon.pokemons));
    const dispatch= useDispatch();
    useEffect(() => {
         
        dispatch(getPokemon)
        
      }, []);
    
   
    setPokemon(useSelector(store =>store.pokemon.pokemons));
   console.log(pokemones)

    return(
        <div className="box">   
           
            <ul className="box">
                {
                    pokemones.map((i) => <PokemonCard key={i.id}
                     
                    name={i.name}
                    tipos={i.types.map(e=>e)}
                    image={i.img}/> )
                            
                }
            </ul>
        </div>
    );
};
